import styled from "styled-components";

export const HeaderContainer = styled.div`
  /* başlık ve 3 arama kutusunu taşıyan kutu */
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background: #00adb5;
`;

export const MainHeader = styled.h2`
  /* food app başlık */
  margin-top: 1rem;
  font-size: 3rem;
`;

export const FormContainer = styled.form`
  /* 3 arama kutusunun toplandığı kutu */
  display: flex;
  /* flex-direction: row; */
  flex-wrap: wrap;
  /* ekran küçülünce sıkışmasınlar,alta geçsinler */
  justify-content: center;
  border: 2px solid white;
  border-radius: 3px;
  margin: 20px;
`;

export const FoodInput = styled.input`
  /* arama yapılacak ürünün yazıldığı input */
  height: 3rem;
  width: 15rem;
  border: none;
  border-radius: 3px;
  /* outline: none; */
  text-indent: 10px;
  /* yazı 10px içten başlasın */
  margin: 5px;
  font-size: 2rem;
`;

export const Button = styled.button`
  /* search butonu */
  background-color: #e1f1dd;
  padding: 5px;
  /* outline: none; */
  height: 3rem;
  border: none;
  margin: 5px;
  border-radius: 3px;
  cursor: pointer;
  font-size: 2rem;
  &:hover {
    background-color: #00adb5;
    transition: all 0.3s ease-in;
    border: 1px solid white;
  }
`;

export const Select = styled.select`
  /* breakfast yazan select */
  border-radius: 3px;
  margin: 5px;
  padding: 5px;
  height: 3rem;
  border: none;
  font-size: 2rem;
`;
